(213, '_transient_redux-extensions-fetch', 'a:15:{s:9:"accordion";a:7:{s:2:"id";i:2598;s:5:"title";s:9:"Accordion";s:7:"created";s:19:"2015-03-26 21:43:11";s:7:"updated";s:19:"2015-05-12 20:13:43";s:7:"excerpt";s:129:"The Redux Accordion extension is an improved way to group like items in your options panel, inside a collapsible accordion field.";s:3:"url";s:47:"https://reduxframework.com/extension/accordion/";s:5:"thumb";s:75:"http://reduxframework.com/wp-content/uploads/edd/2015/03/accordion_logo.png";}s:10:"ad-remover";a:7:{s:2:"id";i:4526;s:5:"title";s:10:"Ad Remover";s:7:"created";s:19:"2016-01-12 04:02:58";s:7:"updated";s:19:"2017-12-13 07:26:32";s:7:"excerpt";s:0:"";s:3:"url";s:48:"https://reduxframework.com/extension/ad-remover/";s:5:"thumb";s:74:"http://reduxframework.com/wp-content/uploads/edd/2016/01/ext_ad_remove.png";}s:13:"color-schemes";a:7:{s:2:"id";i:988;s:5:"title";s:13:"Color Schemes";s:7:"created";s:19:"2014-05-05 20:01:51";s:7:"updated";s:19:"2015-06-13 04:03:02";s:7:"excerpt";s:156:"Give your users the ability to change the color layout of their themes with virtually no effort. Allow your users to save, and re-use any number of presets.";s:3:"url";s:51:"https://reduxframework.com/extension/color-schemes/";s:5:"thumb";s:74:"http://reduxframework.com/wp-content/uploads/edd/2014/07/color_schemes.png";}s:10:"css-layout";a:7:{s:2:"id";i:1684;s:5:"title";s:10:"CSS Layout";s:7:"created";s:19:"2014-08-17 02:52:29";s:7:"updated";s:19:"2017-01-11 01:15:05";s:7:"excerpt";s:183:"The Redux CSS Layout extension is great and innovative new way to set the margin, border, border radius, and padding elements for any CSS selector, all from one easy to use interface.";s:3:"url";s:48:"https://reduxframework.com/extension/css-layout/";s:5:"thumb";s:71:"http://reduxframework.com/wp-content/uploads/edd/2014/08/css-layout.png";}s:9:"date-time";a:7:{s:2:"id";i:458;s:5:"title";s:18:"Date / Time Picker";s:7:"created";s:19:"2014-02-14 02:08:36";s:7:"updated";s:19:"2016-09-